const DeltaIcon = ({
  delta,
  children,
  ...props
}: {
  delta: number;
  children: React.ReactNode;
}) => {
  let deltaIcon;
  if (delta) {
    if (delta < 0)
      deltaIcon = (
        <Flex {...props} width="50%" justifyContent={"end"}>
          <TriangleDownIcon
            mt={1}
            me={"auto"}
            boxSize={4}
            color={"pilotage.red"}
          />
          {children}
        </Flex>
      );
    else if (delta === 0) deltaIcon = <Flex {...props}>{children}</Flex>;
    else
      deltaIcon = (
        <Flex {...props} width="50%" justifyContent={"end"}>
          <TriangleUpIcon
            mt={1}
            me={"auto"}
            boxSize={4}
            color={"success.850"}
          />
          {children}
        </Flex>
      );
  } else {
    deltaIcon = (
      <Flex
        {...props}
        width="50%"
        justifyContent={"end"}
        color={"blueecume.400_active"}
      >
        -
      </Flex>
    );
  }

  return deltaIcon;
};

const IndicateurCompare = ({
  indicateuranneeN,
  indicateuranneeNMoins1,
}: {
  indicateuranneeN?: number;
  indicateuranneeNMoins1?: number;
}) => {
  return (
    <>
      <Text
        px={8}
        color={"blueecume.400_hover"}
        width={"40%"}
        textAlign="end"
        whiteSpace={"nowrap"}
      >
        {indicateuranneeN ?? "-"}
      </Text>
      <DeltaIcon
        delta={(indicateuranneeN || 0) - (indicateuranneeNMoins1 || 0)}
      >
        <Text color={"blueecume.400_active"} whiteSpace={"nowrap"}>
          {indicateuranneeNMoins1 ? `${indicateuranneeNMoins1} / N-1` : "-"}
        </Text>
      </DeltaIcon>
    </>
  );
};

