const DeltaIcon = ({
  delta,
  children,
  ...props
}: {
  delta: number;
  children: React.ReactNode;
}) => {
  let deltaIcon;
  if (delta) {
    if (delta < 0)
      deltaIcon = (
        <Flex {...props} width="50%" justifyContent={"end"}>
          <TriangleDownIcon
            mt={1}
            me={"auto"}
            boxSize={4}
            color={"pilotage.red"}
          />
          {children}
        </Flex>
      );
    else if (delta === 0) deltaIcon = <Flex {...props}>{children}</Flex>;
    else
      deltaIcon = (
        <Flex {...props} width="50%" justifyContent={"end"}>
          <TriangleUpIcon
            mt={1}
            me={"auto"}
            boxSize={4}
            color={"success.850"}
          />
          {children}
        </Flex>
      );
  } else {
    deltaIcon = (
      <Flex
        {...props}
        width="50%"
        justifyContent={"end"}
        color={"blueecume.400_active"}
      >
        -
      </Flex>
    );
  }

  return deltaIcon;
};

